import axios, { AxiosResponse } from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ListadoGenerico from "../peliculas/ListadoGenerico";
import { urlGeneros } from "../peliculas/utilidades/endPoints";
import PaginationComponent from "../peliculas/utilidades/PaginationComponent";
import { genderDTO, genderListDTO } from "./generos.model";

export default function IndiceGeneros() {

 /*  const [genderList, setGenderList] = useState<genderListDTO[]>();
  const [totalPage, setTotalPage] = useState(0);
  const [registryPerPage, setRegistryPerPage] = useState(5);
  const [actualPage, setActualPage] = useState(1);

  useEffect(() => {
   
    
      axios.get(urlGeneros,{
        params: {actualPage, registryPerPage }
      })
        .then(
          (response: AxiosResponse<genderListDTO[]>) => {
            const countRegistryPage = 
            parseInt(response.headers['cantidadtotalregistros'], 10);
            setTotalPage(Math.ceil(countRegistryPage / registryPerPage));
            console.log("esta es: ", registryPerPage )
            console.log(response.data);
            setGenderList(response.data);
           
          }
          
        )
    
    
      
    
  }, [actualPage,registryPerPage]) */
  const [entidades, setEntidades] = useState<genderListDTO[]>();
  const [totalDePaginas, setTotalDePaginas] = useState(0);
  const [recordsPorPagina, setRecordsPorPagina] = useState(10);
  const [pagina, setPagina] = useState(1);

  useEffect(() => {
    cargarDatos();
    // eslint-disable-next-line react-hooks/exhaustive-deps
}, [pagina, recordsPorPagina])

function cargarDatos() {
    axios.get(urlGeneros, {
        params: { pagina, recordsPorPagina }
    })
        .then((respuesta: AxiosResponse<T[]>) => {
            const totalDeRegistros =
                parseInt(respuesta.headers['cantidadtotalregistros'], 10);
            setTotalDePaginas(Math.ceil(totalDeRegistros / recordsPorPagina));
            setEntidades(respuesta.data);
        })
}

  return (
    <>
      <h1>Géneros</h1>
      <Link to="/generos/crear">Crear géneros</Link>

      <PaginationComponent pageAmountTotal={totalPage} actualPage={actualPage}
        onChange={newPage =>  setActualPage(newPage) } />

     

      <ListadoGenerico listado={entidades}  >
        <table className="table table-striped">
          <thead>
            <tr>
              <th >Acciones</th>
              <th>Nombre</th>
            </tr>
          </thead>
          <tbody>
            {genderList?.map(gender => <tr key={gender.id}>
              <td>
                <Link className="btn btn-success" to={`/generos/${gender.id}`}>
                  Editar
                </Link>
                <button className="btn btn-danger">Borrar</button>
              </td>
              <td>
                {gender.nombre}
              </td>
            </tr>)}
          </tbody>
        </table>
      </ListadoGenerico>

    </>

  )
}
