import axios, { AxiosResponse } from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ListadoGenerico from "../peliculas/ListadoGenerico";
import { urlGeneros } from "../peliculas/utilidades/endPoints";
import { generosDTO, listadogenerosDTO } from "./generos.model";

export default function IndiceGeneros() {

  const [genderList, setGenderList] = useState<listadogenerosDTO[]>();
  useEffect(() => {
    axios.get(urlGeneros)
      .then(
        (response: AxiosResponse<listadogenerosDTO[]>) => {
          setGenderList(response.data);
        }
      )
  }, [])
  return (
    <>
      <h1>Géneros</h1>
      <Link to="/generos/crear">Crear géneros</Link>

      <ListadoGenerico listado={genderList}>
        <table className="table table-striped">
          <thead>
            <tr>
              <th >Acciones</th>
              <th>Nombre</th>
            </tr>
          </thead>
          <tbody>
            {genderList?.map(gender => <tr key={gender.id}>
              <td>
                <Link className="btn btn-success" to={`/generos/${gender.id}`}>
                  Editar
                </Link>
                <button className = "btn btn-primary">Borrar</button>
              </td>
              <td>
                {gender.nombre}
              </td>
            </tr>)}
          </tbody>
        </table>
      </ListadoGenerico>

    </>

  )
}
